  gtk_box_pack_start (GTK_BOX (monitor_output_hbox), monitor_output_progress, TRUE, TRUE, 0);
  gtk_progress_configure (GTK_PROGRESS (monitor_output_progress), 0, 0, 0);
  gtk_progress_set_show_text (GTK_PROGRESS (monitor_output_progress), TRUE);
  gtk_progress_set_format_string (GTK_PROGRESS (monitor_output_progress), "%v");

